INSERT INTO `gen_template_info` VALUES (204, 10, 'permissionSql', '## 备注： 变量的使用方法，是mysql语法，基础生成部分是全数据库通用，若数据源非mysql，请只执行基础生成sql\nSTART TRANSACTION;\n\n-- 目录ID\nset @pId = 目录ID;\n-- 菜单ID\nset @menuId = 菜单ID;\n\n## -----------------基础生成部分start-------------\n-- 插入菜单\nINSERT INTO `sys_permission`\n    (`id`, `parent_id`, `title`, `code`, `path`, `router_name`, `component`, `redirect`,  `icon`, `sort`, `keep_alive`, `hidden`, `type`, `deleted`, `create_time`, `update_time`)\nVALUES\n    (@menuId, @pId, \'${comments}\', NULL, \'/${moduleName}/${pathName}\', \'${classname}\', \'${moduleName}/${pathName}/${className}Page\', NULL, NULL, 1, 0, 0, 1, 0, NULL, \'2019-10-13 22:00:24\');\n\n\n-- 菜单对应按钮SQL\nINSERT INTO `sys_permission` ( `id`, `parent_id`,`title`, `code`, `sort`, `type`, `deleted`, `create_time`, `update_time` )\nVALUES\n( @menuId + 1, @menuId, \'${comments}查询\', \'${moduleName}:${pathName}:read\',  0, 2, 0, \'2019-10-13 22:00:24\', NULL );\n\n\nINSERT INTO `sys_permission` ( `id`, `parent_id`,`title`, `code`, `sort`, `type`, `deleted`, `create_time`, `update_time` )\nVALUES\n( @menuId + 2, @menuId, \'${comments}新增\', \'${moduleName}:${pathName}:add\',  1, 2, 0, \'2019-10-13 22:00:24\', NULL );\n\nINSERT INTO `sys_permission` ( `id`, `parent_id`,`title`, `code`, `sort`, `type`, `deleted`, `create_time`, `update_time` )\nVALUES\n( @menuId + 3, @menuId, \'${comments}修改\', \'${moduleName}:${pathName}:edit\', 2, 2, 0, \'2019-10-13 22:00:24\', NULL );\n\nINSERT INTO `sys_permission` ( `id`, `parent_id`,`title`, `code`, `sort`, `type`, `deleted`, `create_time`, `update_time` )\nVALUES\n( @menuId + 4, @menuId, \'${comments}删除\', \'${moduleName}:${pathName}:del\', 3, 2, 0, \'2019-10-13 22:00:24\', NULL );\n\n## -----------------基础生成部分end-------------\n\n-- 清空变量\nset @menuId = null;\nset @pId = null;\n\nCOMMIT;', 1, '权限sql', 0, '2021-01-25 13:11:56', NULL);
INSERT INTO `gen_template_info` VALUES (212, 10, 'Controller', 'package ${packageName}.${moduleName}.controller;\n\nimport ${packageName}.${moduleName}.model.entity.${className};\nimport ${packageName}.${moduleName}.model.qo.${className}QO;\nimport ${packageName}.${moduleName}.model.vo.${className}PageVO;\nimport ${packageName}.${moduleName}.service.${className}Service;\nimport com.hccake.ballcat.commom.log.operation.annotation.CreateOperationLogging;\nimport com.hccake.ballcat.commom.log.operation.annotation.DeleteOperationLogging;\nimport com.hccake.ballcat.commom.log.operation.annotation.UpdateOperationLogging;\nimport com.hccake.ballcat.common.model.domain.PageParam;\nimport com.hccake.ballcat.common.model.domain.PageResult;\nimport com.hccake.ballcat.common.model.result.BaseResultCode;\nimport com.hccake.ballcat.common.model.result.R;\nimport org.springframework.security.access.prepost.PreAuthorize;\nimport io.swagger.annotations.Api;\nimport io.swagger.annotations.ApiOperation;\nimport lombok.RequiredArgsConstructor;\nimport org.springframework.web.bind.annotation.*;\n\n\n/**\n * ${comments}\n *\n * @author ${author} ${currentTime}\n */\n@RestController\n@RequiredArgsConstructor\n@RequestMapping(\"/${moduleName}/${pathName}\" )\n@Api(value = \"${pathName}\", tags = \"${comments}管理\")\npublic class ${className}Controller {\n\n    private final  ${className}Service ${classname}Service;\n\n    /**\n     * 分页查询\n     * @param pageParam 分页参数\n     * @param ${classname}QO ${comments}查询对象\n     * @return R 通用返回体\n     */\n    @ApiOperation(value = \"分页查询\", notes = \"分页查询\")\n    @GetMapping(\"/page\" )\n    @PreAuthorize(\"@per.hasPermission(\'${moduleName}:${pathName}:read\')\" )\n    public R<PageResult<${className}PageVO>> get${className}Page(\n            PageParam pageParam, ${className}QO ${classname}QO) {\n        return R.ok(${classname}Service.queryPage(pageParam, ${classname}QO));\n    }\n\n    /**\n     * 新增${comments}\n     * @param ${classname} ${comments}\n     * @return R 通用返回体\n     */\n    @ApiOperation(value = \"新增${comments}\", notes = \"新增${comments}\")\n    @CreateOperationLogging(msg = \"新增${comments}\" )\n    @PostMapping\n    @PreAuthorize(\"@per.hasPermission(\'${moduleName}:${pathName}:add\')\" )\n    public R save(@RequestBody ${className} ${classname}) {\n        return ${classname}Service.save(${classname}) ?\n                R.ok() : R.failed(BaseResultCode.UPDATE_DATABASE_ERROR, \"新增${comments}失败\");\n    }\n\n    /**\n     * 修改${comments}\n     * @param ${classname} ${comments}\n     * @return R 通用返回体\n     */\n    @ApiOperation(value = \"修改${comments}\", notes = \"修改${comments}\")\n    @UpdateOperationLogging(msg = \"修改${comments}\" )\n    @PutMapping\n    @PreAuthorize(\"@per.hasPermission(\'${moduleName}:${pathName}:edit\')\" )\n    public R updateById(@RequestBody ${className} ${classname}) {\n        return ${classname}Service.updateById(${classname}) ?\n                R.ok() : R.failed(BaseResultCode.UPDATE_DATABASE_ERROR, \"修改${comments}失败\");\n    }\n\n    /**\n     * 通过id删除${comments}\n     * @param ${pk.attrName} id\n     * @return R 通用返回体\n     */\n    @ApiOperation(value = \"通过id删除${comments}\", notes = \"通过id删除${comments}\")\n    @DeleteOperationLogging(msg = \"通过id删除${comments}\" )\n    @DeleteMapping(\"/{${pk.attrName}}\" )\n    @PreAuthorize(\"@per.hasPermission(\'${moduleName}:${pathName}:del\')\" )\n    public R removeById(@PathVariable ${pk.attrType} ${pk.attrName}) {\n        return ${classname}Service.removeById(${pk.attrName}) ?\n                R.ok() : R.failed(BaseResultCode.UPDATE_DATABASE_ERROR, \"通过id删除${comments}失败\");\n    }\n\n}', 1, '控制层', 0, '2021-01-25 13:11:56', '2021-04-01 21:09:39');
INSERT INTO `gen_template_info` VALUES (214, 10, 'Service', 'package ${packageName}.${moduleName}.service;\n\nimport ${packageName}.${moduleName}.model.entity.${className};\nimport ${packageName}.${moduleName}.model.vo.${className}PageVO;\nimport ${packageName}.${moduleName}.model.qo.${className}QO;\nimport com.hccake.ballcat.common.model.domain.PageParam;\nimport com.hccake.ballcat.common.model.domain.PageResult;\nimport com.hccake.extend.mybatis.plus.service.ExtendService;\n\n/**\n * ${comments}\n *\n * @author ${author} ${currentTime}\n */\npublic interface ${className}Service extends ExtendService<${className}> {\n\n    /**\n    *  根据QueryObeject查询分页数据\n    * @param pageParam 分页参数\n    * @param qo 查询参数对象\n    * @return PageResult&lt;${className}PageVO&gt; 分页数据\n    */\n    PageResult<${className}PageVO> queryPage(PageParam pageParam, ${className}QO qo);\n\n}', 1, '业务逻辑层', 0, '2021-01-25 13:11:56', '2021-04-01 21:10:49');
INSERT INTO `gen_template_info` VALUES (215, 10, 'ServiceImpl', 'package ${packageName}.${moduleName}.service.impl;\n\nimport ${packageName}.${moduleName}.model.entity.${className};\nimport ${packageName}.${moduleName}.model.vo.${className}PageVO;\nimport ${packageName}.${moduleName}.model.qo.${className}QO;\nimport ${packageName}.${moduleName}.mapper.${className}Mapper;\nimport ${packageName}.${moduleName}.service.${className}Service;\nimport com.hccake.ballcat.common.model.domain.PageParam;\nimport com.hccake.ballcat.common.model.domain.PageResult;\nimport com.hccake.extend.mybatis.plus.service.impl.ExtendServiceImpl;\nimport org.springframework.stereotype.Service;\n\n/**\n * ${comments}\n *\n * @author ${author} ${currentTime}\n */\n@Service\npublic class ${className}ServiceImpl extends ExtendServiceImpl<${className}Mapper, ${className}> implements ${className}Service {\n\n    /**\n    *  根据QueryObeject查询分页数据\n    * @param pageParam 分页参数\n    * @param qo 查询参数对象\n    * @return PageResult<${className}PageVO> 分页数据\n    */\n    @Override\n    public PageResult<${className}PageVO> queryPage(PageParam pageParam, ${className}QO qo) {\n        return baseMapper.queryPage(pageParam, qo);\n    }\n\n}\n', 1, '业务逻辑层实现类', 0, '2021-01-25 13:11:56', '2021-04-01 21:11:20');
INSERT INTO `gen_template_info` VALUES (216, 10, 'Mapper', 'package ${packageName}.${moduleName}.mapper;\n\nimport com.baomidou.mybatisplus.core.metadata.IPage;\nimport ${packageName}.${moduleName}.converter.${className}Converter;\nimport ${packageName}.${moduleName}.model.entity.${className};\nimport ${packageName}.${moduleName}.model.qo.${className}QO;\nimport ${packageName}.${moduleName}.model.vo.${className}PageVO;\nimport com.hccake.ballcat.common.model.domain.PageParam;\nimport com.hccake.ballcat.common.model.domain.PageResult;\nimport com.hccake.extend.mybatis.plus.conditions.query.LambdaQueryWrapperX;\nimport com.hccake.extend.mybatis.plus.mapper.ExtendMapper;\nimport com.hccake.extend.mybatis.plus.toolkit.WrappersX;\n\n/**\n * ${comments}\n *\n * @author ${author} ${currentTime}\n */\npublic interface ${className}Mapper extends ExtendMapper<${className}> {\n\n    /**\n    * 分页查询\n    * @param pageParam 分页参数\n    * @param qo 查询参数\n    * @return PageResult<${className}PageVO> VO分页数据\n    */\n   default PageResult<${className}PageVO> queryPage(PageParam pageParam, ${className}QO qo){\n		IPage<${className}> page = this.prodPage(pageParam);\n		LambdaQueryWrapperX<${className}> wrapper = WrappersX.lambdaQueryX(${className}.class);\n		this.selectPage(page, wrapper);\n		IPage<${className}PageVO> voPage = page.convert(${className}Converter.INSTANCE::poToPageVo);\n		return new PageResult<>(voPage.getRecords(), voPage.getTotal());\n   }\n}', 1, '数据访问层', 0, '2021-01-25 13:11:56', '2021-04-01 21:43:24');
INSERT INTO `gen_template_info` VALUES (220, 10, 'Entity', 'package ${packageName}.${moduleName}.model.entity;\n\nimport com.baomidou.mybatisplus.annotation.FieldFill;\nimport com.baomidou.mybatisplus.annotation.TableField;\nimport com.baomidou.mybatisplus.annotation.TableId;\nimport com.baomidou.mybatisplus.annotation.TableName;\nimport com.baomidou.mybatisplus.extension.activerecord.Model;\nimport io.swagger.annotations.ApiModel;\nimport io.swagger.annotations.ApiModelProperty;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\nimport java.io.Serializable;\nimport java.time.LocalDateTime;\n\n/**\n * ${comments}\n *\n * @author ${author} ${currentTime}\n */\n@Data\n@TableName(\"${tableName}\")\n@ApiModel(value = \"${comments}\")\npublic class ${className} {\n\n	private static final long serialVersionUID = 1L;\n\n#foreach ($column in $columns)\n	/**\n	 * $column.comments\n	 */\n#if($column.columnName == $pk.columnName)\n	@TableId\n#elseif($column.columnName == \'create_time\')\n	@TableField(fill = FieldFill.INSERT)\n#elseif($column.columnName == \'update_time\')\n	@TableField(fill = FieldFill.INSERT_UPDATE)\n#end\n	@ApiModelProperty(value=\"$column.comments\")\n	private $column.attrType $column.attrName;\n    \n#end\n\n}\n', 1, '数据实体', 0, '2021-01-25 13:11:56', '2021-04-06 17:59:22');
INSERT INTO `gen_template_info` VALUES (221, 10, 'QO', 'package ${packageName}.${moduleName}.model.qo;\n\nimport io.swagger.annotations.ApiModel;\nimport io.swagger.annotations.ApiModelProperty;\nimport lombok.Data;\n\n/**\n * ${comments} 查询对象\n *\n * @author ${author} ${currentTime}\n */\n@Data\n@ApiModel(value = \"${comments}查询对象\")\npublic class ${className}QO  {\n	private static final long serialVersionUID = 1L;\n\n#foreach ($column in $columns)\n	/**\n	 * $column.comments\n	 */\n#if($column.columnName == $pk.columnName)\n	@ApiModelProperty(value=\"$column.comments\")\n	private $column.attrType $column.attrName;\n#break\n#end\n#end\n\n}', 1, '默认数据查询对象', 0, '2021-01-25 13:11:56', '2021-04-01 21:07:52');
INSERT INTO `gen_template_info` VALUES (222, 10, 'VO', 'package ${packageName}.${moduleName}.model.vo;\n\nimport com.baomidou.mybatisplus.extension.activerecord.Model;\nimport io.swagger.annotations.ApiModel;\nimport io.swagger.annotations.ApiModelProperty;\nimport lombok.Data;\nimport lombok.EqualsAndHashCode;\n    #if(${hasBigDecimal})\n    import java.math.BigDecimal;\n    #end\nimport java.io.Serializable;\nimport java.time.LocalDateTime;\n\n/**\n * ${comments}分页视图对象\n *\n * @author ${author} ${currentTime}\n */\n@Data\n@ApiModel(value = \"${comments}分页视图对象\")\npublic class ${className}PageVO {\n    private static final long serialVersionUID = 1L;\n\n#foreach ($column in $columns)\n    /**\n     * $column.comments\n     */\n    @ApiModelProperty(value=\"$column.comments\")\n    private $column.attrType $column.attrName;\n    \n#end\n\n}', 1, '默认数据视图对象', 0, '2021-01-25 13:11:56', '2021-04-01 21:06:03');
INSERT INTO `gen_template_info` VALUES (225, 10, 'Mapper.xml', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${packageName}.${moduleName}.mapper.${className}Mapper\">\n\n	<resultMap id=\"${classname}Map\" type=\"${packageName}.${moduleName}.model.entity.${className}\">\n#foreach($column in $columns)\n  #if($column.attrName==$pk.attrName)\n		<id property=\"${pk.attrName}\" column=\"${pk.columnName}\"/>\n  #else\n		<result property=\"${column.attrName}\" column=\"${column.columnName}\"/>\n  #end\n#end\n    </resultMap>\n\n	<sql id=\"Base_Column_List\">\n#foreach($column in $columns)\n		${column.columnName}#if($foreach.hasNext),#end\n        \n#end\n	</sql>\n\n	<sql id=\"Base_Alias_Column_List\">\n#foreach($column in $columns)\n		${tableAlias}.${column.columnName}#if( $foreach.hasNext ),#end\n        \n#end\n	</sql>\n</mapper>', 1, 'Mybatis Mapper Xml', 0, '2021-01-25 13:11:56', '2021-04-01 21:36:55');
INSERT INTO `gen_template_info` VALUES (230, 10, 'api.js', 'import request from \'@/utils/request\'\n\nexport function getPage(query) {\n  return request({\n    url: \'/${moduleName}/${pathName}/page\',\n    method: \'get\',\n    params: query\n  })\n}\n\nexport function addObj(obj) {\n  return request({\n    url: \'/${moduleName}/${pathName}\',\n    method: \'post\',\n    data: obj\n  })\n}\n\nexport function delObj(id) {\n  return request({\n    url: \'/${moduleName}/${pathName}/\' + id,\n    method: \'delete\'\n  })\n}\n\nexport function putObj(obj) {\n  return request({\n    url: \'/${moduleName}/${pathName}\',\n    method: \'put\',\n    data: obj\n  })\n}', 1, 'Axios 访问方法', 0, '2021-01-25 13:11:56', NULL);
INSERT INTO `gen_template_info` VALUES (231, 10, 'ModalForm.vue', '<template>\n  <a-modal\n    :title=\"title\"\n    :visible=\"visible\"\n    :mask-closable=\"false\"\n    :body-style=\"{paddingBottom: \'8px\'}\"\n    :confirm-loading=\"submitLoading\"\n    @ok=\"handleSubmit\"\n    @cancel=\"handleClose\"\n  >\n    <a-form\n      :form=\"form\"\n      :label-col=\"labelCol\"\n      :wrapper-col=\"wrapperCol\"\n    >\n#foreach ($column in $columns)\n#if($column.columnName == $pk.attrName)\n      <a-form-item v-if=\"formAction === FORM_ACTION.UPDATE\" style=\"display: none\">\n        <a-input v-decorator=\"[\'${pk.attrName}\']\" />\n      </a-form-item>\n#elseif($column.columnName != \"create_time\" && $column.columnName != \"update_time\")\n      <a-form-item label=\"$column.comments\">\n        <a-input v-decorator=\"[\'$column.attrName\']\" placeholder=\"$column.comments\" />\n      </a-form-item>\n#end\n#end\n    </a-form>\n  </a-modal>\n</template>\n\n<script>\nimport { PopUpFormMixin } from \'@/mixins\'\nimport { addObj, putObj } from \'@/api/${moduleName}/${pathName}\'\n\nexport default {\n  name: \'${className}ModalForm\',\n  mixins: [PopUpFormMixin],\n  data () {\n    return {\n      reqFunctions: {\n        create: addObj,\n        update: putObj\n      },\n\n      labelCol: {\n        sm: { span: 24 },\n        md: { span: 5 }\n      },\n      wrapperCol: {\n        sm: { span: 24 },\n        md: { span: 19 }\n      },\n\n      // 校验配置\n      decoratorOptions: {}\n    }\n  },\n  methods: {}\n}\n</script>', 1, '新建和修改使用的表单页面', 0, '2021-01-25 13:11:56', '2021-04-01 21:33:53');
INSERT INTO `gen_template_info` VALUES (232, 10, 'Page.vue', '<template>\n  <div class=\"ant-pro-page-container-children-content\">\n    <!-- 查询条件 -->\n    <div class=\"ant-pro-table-search\">\n      <a-form v-bind=\"searchFormLayout\">\n        <a-row :gutter=\"16\">\n          <a-col :md=\"8\" :sm=\"24\">\n            <a-form-item label=\"ID\">\n              <a-input v-model=\"queryParam.${pk.attrName}\" placeholder=\"\" />\n            </a-form-item>\n          </a-col>\n\n          <!-- <template v-if=\"advanced\">\n           </template>-->\n          <a-col :md=\"8\" :sm=\"24\" class=\"table-page-search-wrapper\">\n            <div class=\"table-page-search-submitButtons\">\n              <a-button type=\"primary\" @click=\"reloadTable\">查询</a-button>\n              <a-button style=\"margin-left: 8px\" @click=\"resetSearchForm\">重置</a-button>\n              <!--<a @click=\"toggleAdvanced\" style=\"margin-left: 8px\">\n                {{ advanced ? \'收起\' : \'展开\' }}\n                <a-icon :type=\"advanced ? \'up\' : \'down\'\"/>\n              </a>-->\n            </div>\n          </a-col>\n        </a-row>\n      </a-form>\n    </div>\n\n    <a-card :bordered=\"false\" :body-style=\"{padding: 0}\">\n      <!-- 操作按钮区域 -->\n      <div class=\"ant-pro-table-toolbar\">\n        <div class=\"ant-pro-table-toolbar-title\">${comments}</div>\n        <div class=\"ant-pro-table-toolbar-option\">\n          <a-button\n            v-has=\"\'${moduleName}:${pathName}:add\'\"\n            type=\"primary\"\n            icon=\"plus\"\n            @click=\"handleAdd()\"\n          >新建\n          </a-button>\n        </div>\n      </div>\n\n      <!--数据表格区域-->\n      <div class=\"ant-pro-table-wrapper\">\n        <a-table\n          ref=\"table\"\n          size=\"middle\"\n          :row-key=\"rowKey\"\n          :columns=\"columns\"\n          :data-source=\"dataSource\"\n          :pagination=\"pagination\"\n          :loading=\"loading\"\n          @change=\"handleTableChange\"\n        >\n          <template #action-slot=\"text, record\">\n            <a v-has=\"\'${moduleName}:${pathName}:edit\'\" @click=\"handleEdit(record)\">编辑</a>\n            <a-divider type=\"vertical\" />\n            <a-popconfirm \n            	v-has=\"\'${moduleName}:${pathName}:del\'\"\n                title=\"确认要删除吗？\"\n                @confirm=\"() => handleDel(record)\"\n             >\n              <a href=\"javascript:\" class=\"ballcat-text-danger\">删除</a>\n            </a-popconfirm>\n          </template>\n        </a-table>\n      </div>\n    </a-card>\n\n    <!--表单弹窗-->\n    <${path}-modal-form ref=\"formModal\" @reload-page-table=\"reloadTable\"/>\n  </div>\n</template>\n\n<script>\nimport { getPage, delObj } from \'@/api/${moduleName}/${pathName}\'\nimport { TablePageMixin } from \'@/mixins\'\nimport ${className}ModalForm from \'@/views/${moduleName}/${pathName}/${className}ModalForm\'\n\nexport default {\n  name: \'${className}Page\',\n  components: { ${className}ModalForm },\n  mixins: [TablePageMixin],\n  data () {\n    return {\n      getPage: getPage,\n      delObj: delObj,\n\n      columns: [\n#foreach($column in $columns)\n#if($column.columnName != \'create_time\' && $column.columnName != \'update_time\')\n#if($column.columnName == \'id\')\n        {\n          title: \'#\',\n          dataIndex: \'$column.attrName\'\n        },\n#else\n        {\n          title: \'$column.comments\',\n          dataIndex: \'$column.attrName\'\n        },\n#end\n#end\n#end\n        {\n          title: \'创建时间\',\n          dataIndex: \'createTime\',\n          width: \'150px\',\n          sorter: true\n        },\n        {\n          title: \'操作\',\n          align: \'center\',\n          width: \'150px\',\n          scopedSlots: { customRender: \'action-slot\' }\n        }\n      ]\n    }\n  },\n  methods: {\n    /**\n     * 新建${comments}\n     */\n    handleAdd () {\n      const attributes = { title: \'新建${comments}\' }\n      this.$refs.formModal.add(attributes)\n    },\n    /**\n     * 编辑${comments}\n     * @param record 当前${comments}属性\n     */\n    handleEdit (record) {\n      const attributes = { title: \'编辑${comments}\' }\n      this.$refs.formModal.update(record, attributes)\n    }\n  }\n}\n</script>', 1, '查询页面', 0, '2021-01-25 13:11:56', '2021-04-02 17:07:41');
INSERT INTO `gen_template_info` VALUES (359, 10, 'Converter', 'package ${packageName}.${moduleName}.converter;\n\nimport ${packageName}.${moduleName}.model.entity.${className};\nimport ${packageName}.${moduleName}.model.vo.${className}PageVO;\nimport org.mapstruct.Mapper;\nimport org.mapstruct.factory.Mappers;\n\n/**\n * ${comments}模型转换器\n *\n * @author ${author} ${currentTime}\n */\n@Mapper\npublic interface ${className}Converter {\n\n	${className}Converter INSTANCE = Mappers.getMapper(${className}Converter.class);\n\n	/**\n	 * PO 转 PageVO\n	 * @param ${classname} ${comments}\n	 * @return ${className}PageVO ${comments}PageVO\n	 */\n	${className}PageVO poToPageVo(${className} ${classname});\n\n}\n', 1, '类型转换器', 0, '2021-03-22 18:51:56', '2021-03-22 20:34:16');
